import React, { useEffect, Fragment, useState } from 'react';
import { Button, Table } from 'antd';
import './DashboardViTricss/DashboardViTri.css';
import { AudioOutlined, DeleteOutlined, EditOutlined, SearchOutlined, CalendarOutlined } from '@ant-design/icons';
import { Input } from 'antd';
import { useDispatch, useSelector } from 'react-redux';
import { NavLink, useNavigate } from 'react-router-dom';
import { actFetchDanhSachViTri, actSearchViTriDashboard } from './duckViTri/actDashboardViTri';
import { actFetchDeleteViTRi } from '../deleteViTri/actDeleteVitri';
import { API_IMG_URL } from 'Utils/_constantsUtils';
import ButtonUploadImage from '../editViTri/ButtonUploadImage';


const { Search } = Input;
const suffix = (
    <AudioOutlined style={{ fontSize: 16, color: '#1677ff', }} />
);

export default function DashboardViTri() {
    const navigate = useNavigate();
    const dispatch = useDispatch();
    const { searchData } = useSelector(state => state.layDanhSachViTriReducer);

    useEffect(() => {
        dispatch(actFetchDanhSachViTri());
    }, [])


    const onSearch = (value) => {
        // gọi api edit 
        dispatch(actSearchViTriDashboard(value));
    };


    const columns = [
        {
            title: 'id',
            dataIndex: 'vitri_id',
            width: '5%'
        },
        {
            title: 'Tên Vị Trí',
            dataIndex: 'ten_vi_tri',
            filterSearch: true,
            onFilter: (value, record) => record.vitri.startsWith(value),
            width: '15%',
            sorter: (a, b) => {
                let vitriA = a.vitri.toLowerCase().trim();
                let vitriB = b.vitri.toLowerCase().trim();
                if (vitriA < vitriB) {
                    return 1
                }
                return -1
            },
        },
        {
            title: 'Hình Ảnh',
            dataIndex: 'hinh_anh',
            onFilter: (value, record) => record.hinh_anh.startsWith(value),
            filterSearch: true,
            render: (text, vitri) => {
                return <Fragment>
                    <div className='grid grid-cols-2'>
                        <div>
                            <img src={API_IMG_URL + vitri.hinh_anh} alt={vitri.ten_phong} key={vitri.vitri_id} width={100} height={100} onError={({ currentTarget }) => {
                                currentTarget.onerror = null; // prevents looping
                                currentTarget.src = "data:image/png;base64,";
                            }} />
                        </div>
                        <div>
                            <span className='underline cursor-pointer hover:text-pink-400 transition-all duration-500' >Thêm Hình Ảnh</span>
                        </div>
                    </div>
                </Fragment>
            },
            width: '25%',
        },
        {
            title: 'Tỉnh Thành',
            dataIndex: 'tinh_thanh',
            sorter: (a, b) => a.tinh_thanh - b.tinh_thanh,
            sortDirections: ["descend", "ascend"],
            width: '10%',
        },
        {
            title: 'Action',
            dataIndex: 'hanhDong',
            render: (words, vitri) => {
                return <Fragment>
                    <NavLink key={1} to={`/admin/quanlyvitri/edit-vitri/${vitri?.vitri_id}`}>
                        <EditOutlined className='p-2 text-green-600 text-lg text-right' />
                    </NavLink>
                    <span className='cursor-pointer' key={6} onClick={() => {
                        //gọi action xóa
                        if (window.confirm(`Bạn chắc chắn xóa vị trí ${vitri?.ten_vi_tri} này!?`)) {
                            //gọi action Xóa phim
                            dispatch(actFetchDeleteViTRi(vitri?.vitri_id));
                        }
                    }}>
                        <DeleteOutlined className='p-2 text-red-700 text-lg text-right' />
                    </span>
                    <NavLink key={2} to={`/admin/quanlyvitri/info-vitri/${vitri?.vitri_id}`} className='navLinkDSND'>
                        <CalendarOutlined />
                        <p className='navLinkDSND__child'>Xem Chi Tiết</p>
                    </NavLink>
                </Fragment>
            },
            width: '15%'
        }
    ];

    const onChange = (pagination, filters, sorter, extra) => {
        // console.log('params', pagination, filters, sorter, extra);
    };
    return (
        <div>
            <h3 className='text-left mb-1 text-2xl font-bold'>Quản lý Vị Trí</h3>
            <Button className='mb-2' onClick={() => {
                navigate('/admin/quanlyvitri/add-vitri')
            }}>Thêm vị Trí</Button>
            <Search
                style={{ backgroundColor: "black", borderRadius: "5px" }}
                placeholder="Nhập vào tên vị trí"
                allowClear
                enterButton={<SearchOutlined className='pb-2' />}
                size="large"
                onSearch={onSearch}
                className='mt-2 mb-4'
            />
            <Table columns={columns} dataSource={searchData} onChange={onChange} rowKey='vitri_id' />
        </div>
    )
}
